const items = $input.all().map(i => i.json);

// Filter out empty results
const locations = items.filter(i => i.Location_ID);

if (locations.length === 0) {
  return [{ json: { found: false } }];
}

// Take first location
return [{ json: { found: true, location: locations[0] } }];

const location = $('IF Location Found').first().json.location;
const contentRows = $input.all().map(i => i.json);

// Check if postcard exists (indicates initial run already done)
const postcardExists = contentRows.some(r => r.Content_Type === 'postcard');

// Check for video redos
const redoItems = contentRows.filter(r => r.Video_Status === 'Redo');

return [{
  json: {
    location: location,
    contentRows: contentRows,
    isInitialRun: !postcardExists,
    hasRedos: redoItems.length > 0,
    redoItems: redoItems
  }
}];

const data = $('Determine Run Type').first().json;

return [{
  json: {
    locationName: data.location.Name,
    locationId: data.location.Location_ID,
    sanitizedName: data.location.Name.replace(/\s+/g, '_')
  }
}];

const uploadResult = $json;
const prepData = $('Prepare Postcard').first().json;

return [{
  json: {
    Content_ID: `${prepData.sanitizedName}_postcard`,
    Location_ID: prepData.locationId,
    Content_Type: 'postcard',
    Image_Status: 'Review',
    Video_Status: 'N/A',
    Drive_ID: uploadResult.id
  }
}];

const response = $json;
const data = $('Determine Run Type').first().json;

return [{
  json: {
    taskId: response.data.taskId,
    musicStartTime: Date.now(),
    locationName: data.location.Name,
    locationId: data.location.Location_ID,
    sanitizedName: data.location.Name.replace(/\s+/g, '_')
  }
}];

const statusResponse = $json;
const prevData = $('Set Music Start Time').first().json;

const status = statusResponse.data?.status || 'PENDING';
const isSuccess = status === 'SUCCESS';
const isFailed = status === 'FAILED' || statusResponse.data?.errorCode;

const elapsed = Date.now() - prevData.musicStartTime;
const fiveMinutes = 5 * 60 * 1000;
const isTimedOut = elapsed > fiveMinutes && !isSuccess;

let audioUrl = null;
if (isSuccess && statusResponse.data?.response?.sunoData?.[0]?.audioUrl) {
  audioUrl = statusResponse.data.response.sunoData[0].audioUrl;
}

return [{
  json: {
    ...prevData,
    status,
    isSuccess,
    isFailed,
    isTimedOut,
    elapsed,
    audioUrl,
    statusResponse
  }
}];

const prevData = $('Set Music Start Time').first().json;

// Build snapshot filenames
const snapshots = [];
for (let i = 1; i <= 10; i++) {
  snapshots.push({
    index: i,
    filename: `${prevData.sanitizedName}_snapshot_${String(i).padStart(2, '0')}.png`
  });
}

// Split into two groups
const slideshow1 = snapshots.slice(0, 5);
const slideshow2 = snapshots.slice(5, 10);

return [{
  json: {
    musicPath: '/mnt/data/tmp/music.mp3',
    locationName: prevData.locationName,
    locationId: prevData.locationId,
    sanitizedName: prevData.sanitizedName,
    slideshows: [
      {
        index: 1,
        filename: `${prevData.sanitizedName}_slideshow_01.mp4`,
        images: slideshow1
      },
      {
        index: 2,
        filename: `${prevData.sanitizedName}_slideshow_02.mp4`,
        images: slideshow2
      }
    ]
  }
}];

const slideshow = $json.slideshows;
const images = slideshow.images;
const contentRows = $('Get Content Rows').all().map(item => item.json);

const startIndex = slideshow.index === 1 ? 1 : 6;

const imageDownloads = images.map((img, i) => {
  const snapshotNum = String(startIndex + i).padStart(2, '0');
  const contentRow = contentRows.find(r => r.Content_Type === `snapshot_${snapshotNum}`);
  const rawCaption = contentRow?.Polaroid_Caption || '';
  const caption = rawCaption.replace(/'/g, "'\\''");

  return {
    filename: img.filename,
    index: i,
    caption: caption,
    outputPath: `/mnt/data/tmp/slideshow_${slideshow.index}_img_${i}.png`,
    polaroidPath: `/mnt/data/tmp/slideshow_${slideshow.index}_polaroid_${i}.png`
  };
});

return {
  json: {
    slideshowIndex: slideshow.index,
    filename: slideshow.filename,
    imageDownloads: imageDownloads
  }
};

const allItems = $input.all();
const slideshowData = $('Prepare Slideshow Downloads').all().map(i => i.json);

const uniqueSlideshows = [...new Set(slideshowData.map(s => s.slideshowIndex))];

return uniqueSlideshows.map(idx => {
  const slideshow = slideshowData.find(s => s.slideshowIndex === idx);
  return {
    json: {
      slideshowIndex: idx,
      filename: slideshow.filename,
      polaroidPattern: `/mnt/data/tmp/slideshow_${idx}_polaroid_%d.png`,
      outputPath: `/mnt/data/tmp/slideshow_${idx}.mp4`
    }
  };
});

const slideshowData = $('Collect Polaroids').item.json;
const uploadResult = $json;
const prepData = $('Prepare Slideshows').first().json;
const contentId = `${prepData.sanitizedName}_slideshow_0${slideshowData.slideshowIndex}`;

// Pick random title and caption
const titles = $('Get Slideshow Titles').all().map(i => i.json.Value);
const captions = $('Get Slideshow Captions').all().map(i => i.json.Value);
const title = titles[Math.floor(Math.random() * titles.length)].replace('{location}', prepData.locationName);
const caption = captions[Math.floor(Math.random() * captions.length)].replace('{location}', prepData.locationName);

return {
  json: {
    Content_ID: contentId,
    Location_ID: prepData.locationId,
    Content_Type: `slideshow_0${slideshowData.slideshowIndex}`,
    Title: title,
    Caption: caption,
    Image_Status: 'N/A',
    Video_Status: 'Review',
    Drive_ID: uploadResult.id
  }
};

const slideshows = $input.all().map(item => item.json);

return [{
  json: {
    slideshows: slideshows,
    slideshowCount: slideshows.length
  }
}];

const contentRows = $('Get Content Rows').all().map(i => i.json);
const location = $('IF Location Found').first().json.location;
const sanitizedName = location.Name.replace(/\s+/g, '_');

const intro = contentRows.find(r => r.Content_Type === 'intro');
const joke01 = contentRows.find(r => r.Content_Type === 'joke_01');
const joke02 = contentRows.find(r => r.Content_Type === 'joke_02');
const review = contentRows.find(r => r.Content_Type === 'review');

const videoItems = [];

// Intro (2 clips)
videoItems.push({
  contentType: 'intro',
  contentId: intro.Content_ID,
  imageFilename: `${sanitizedName}_intro.png`,
  videoFilename: `${sanitizedName}_intro.mp4`,
  videoPrompt1: intro.Video_Prompt_1,
  videoPrompt2: intro.Video_Prompt_2,
  needsStitch: true
});

// Jokes (1 clip each)
[joke01, joke02].forEach((joke, i) => {
  videoItems.push({
    contentType: `joke_0${i + 1}`,
    contentId: joke.Content_ID,
    imageFilename: `${sanitizedName}_joke_0${i + 1}.png`,
    videoFilename: `${sanitizedName}_joke_0${i + 1}.mp4`,
    videoPrompt: joke.Video_Prompt_1,
    needsStitch: false
  });
});

// Review (2 clips)
videoItems.push({
  contentType: 'review',
  contentId: review.Content_ID,
  imageFilename: `${sanitizedName}_review.png`,
  videoFilename: `${sanitizedName}_review.mp4`,
  videoPrompt1: review.Video_Prompt_1,
  videoPrompt2: review.Video_Prompt_2,
  needsStitch: true
});

return [{
  json: {
    locationId: location.Location_ID,
    locationName: location.Name,
    sanitizedName: sanitizedName,
    videoItems: videoItems
  }
}];

const videoData = $('Split Videos').item.json.videoItems;
const fileId = $json.id;

return {
  json: {
    ...videoData,
    imageUrl: `https://drive.google.com/uc?export=view&id=${fileId}`
  }
};

const videoData = $('Build Image URL').item.json;

return {
  json: {
    ...videoData,
    clip1TaskId: $json.data.taskId,
    clip1StartTime: Date.now()
  }
};

const statusResponse = $input.item.json;
const allVideoData = $('Set Clip 1 Start Time').all();
const videoData = allVideoData.find(v => v.json.clip1TaskId === statusResponse.data?.taskId)?.json || {};

const data = statusResponse.data;
const successFlag = data?.successFlag;
const fiveMinutes = 5 * 60 * 1000;
const elapsed = Date.now() - videoData.clip1StartTime;

let videoUrl = null;
if (data?.response?.resultUrls?.[0]) {
  videoUrl = data.response.resultUrls[0];
}

return {
  json: {
    ...videoData,
    status: successFlag === 1 ? 'success' : (data?.errorCode ? 'failed' : 'pending'),
    elapsed: elapsed,
    isTimedOut: elapsed > fiveMinutes && successFlag !== 1,
    isFailed: data?.errorCode !== null,
    isSuccess: successFlag === 1,
    videoUrl: videoUrl,
    statusResponse: data
  }
};

const statusItems = $input.first().json.items;
const originalItems = $('Set Clip 1 Start Time').all().map(i => i.json);

const items = statusItems.map(status => {
  const original = originalItems.find(o => o.clip1TaskId === status.taskId);
  const elapsed = original ? Date.now() - original.clip1StartTime : 0;
  const fiveMinutes = 5 * 60 * 1000;

  return {
    ...status,
    ...original,
    elapsed: elapsed,
    isTimedOut: elapsed > fiveMinutes && !status.isSuccess
  };
});

const successCount = items.filter(i => i.isSuccess).length;
const failedItems = items.filter(i => i.isFailed);
const timedOutItems = items.filter(i => i.isTimedOut && !i.isFailed);
const pendingItems = items.filter(i => !i.isSuccess && !i.isFailed && !i.isTimedOut);

return [{
  json: {
    allSuccess: successCount === items.length,
    anyFailed: failedItems.length > 0,
    anyTimedOut: timedOutItems.length > 0,
    totalItems: items.length,
    successCount,
    failedCount: failedItems.length,
    timedOutCount: timedOutItems.length,
    pendingCount: pendingItems.length,
    failedItems,
    timedOutItems,
    items
  }
}];

const videoData = $('Split Clip 1 Results').item.json;

return {
  json: {
    ...videoData,
    clip2TaskId: $json.data.taskId,
    clip2StartTime: Date.now()
  }
};

const statusResponse = $input.item.json;
const data = statusResponse.data;
const successFlag = data?.successFlag;
const elapsed = data?.createTime ? Date.now() - data.createTime : 0;
const fiveMinutes = 5 * 60 * 1000;

// Get video URL if available
let videoUrl = null;
if (data?.response?.resultUrls?.[0]) {
  videoUrl = data.response.resultUrls[0];
}

return {
  json: {
    taskId: data?.taskId,
    status: successFlag === 1 ? 'success' : (data?.errorCode ? 'failed' : 'pending'),
    elapsed: elapsed,
    isTimedOut: elapsed > fiveMinutes && successFlag !== 1,
    isFailed: data?.errorCode !== null,
    isSuccess: successFlag === 1,
    clip2VideoUrl: videoUrl,
    statusResponse: data
  }
};

const statusItems = $input.first().json.items;
const originalItems = $('Set Clip 2 Start Time').all().map(i => i.json);

const items = statusItems.map(status => {
  const original = originalItems.find(o => o.clip2TaskId === status.taskId);
  const elapsed = original ? Date.now() - original.clip2StartTime : 0;
  const fiveMinutes = 5 * 60 * 1000;

  return {
    ...original,
    ...status,
    elapsed: elapsed,
    isTimedOut: elapsed > fiveMinutes && !status.isSuccess
  };
});

const successCount = items.filter(i => i.isSuccess).length;
const failedItems = items.filter(i => i.isFailed);
const timedOutItems = items.filter(i => i.isTimedOut && !i.isFailed);
const pendingItems = items.filter(i => !i.isSuccess && !i.isFailed && !i.isTimedOut);

return [{
  json: {
    allSuccess: successCount === items.length,
    anyFailed: failedItems.length > 0,
    anyTimedOut: timedOutItems.length > 0,
    totalItems: items.length,
    successCount,
    failedCount: failedItems.length,
    timedOutCount: timedOutItems.length,
    pendingCount: pendingItems.length,
    failedItems,
    timedOutItems,
    items
  }
}];

const item = $input.item.json;
const originalData = $('Split Clip 2 Results').item.json;

return {
  json: {
    ...item,
    contentType: originalData.contentType,
    contentId: originalData.contentId,
    filename: `${sanitizedName}_${originalData.contentType}.mp4`
  },
  binary: $input.item.binary
};

const item = $input.item.json;
const originalData = $('Split Clip 1 Results').item.json;

return {
  json: {
    ...item,
    contentType: originalData.contentType,
    contentId: originalData.contentId,
    filename: originalData.videoFilename
  },
  binary: $input.item.binary
};

const data = $('Determine Run Type').first().json;
const redoItems = data.redoItems;
const location = data.location;
const sanitizedName = location.Name.replace(/\s+/g, '_');

const videoItems = redoItems.map(item => {
  const contentType = item.Content_Type;
  const needsStitch = ['intro', 'review'].includes(contentType);

  return {
    contentType: contentType,
    contentId: item.Content_ID,
    imageFilename: `${sanitizedName}_${contentType}.png`,
    videoFilename: `${sanitizedName}_${contentType}.mp4`,
    videoPrompt1: item.Video_Prompt_1,
    videoPrompt2: item.Video_Prompt_2 || null,
    videoPrompt: needsStitch ? null : item.Video_Prompt_1,
    needsStitch: needsStitch
  };
});

return [{
  json: {
    locationId: location.Location_ID,
    locationName: location.Name,
    sanitizedName: sanitizedName,
    videoItems: videoItems
  }
}];

